function latestArticles(e){var t="";$.each(e,(function(e,a){t+='<article><img src="',t+=a.thumbnail,t+='"><span rel="author">By ',t+=a.author,t+="</span><h1>",t+=a.title,t+="</h1><p>",t+=a.content,t+="</p></article>"})),$(".latest-articles").html(t)}$(".menu").click((function(){$("nav").fadeToggle(50),$(".overlay").fadeToggle(200),$(".menu").toggleClass("close"),$("body").toggleClass("hide-overflow")})),$((function(){$.ajax({type:"GET",url:"articles.json",async:!1,beforeSend:function(){},dataType:"json",success:function(e){latestArticles(e)}})}));